mapper.select:
SELECT store_code, target_date, from_time, to_time, demand, task_code
FROM register_demand_interval
WHERE store_code = #{storeCode}
  AND target_date = #{targetDate}
ORDER BY from_time

SELECT store_code, target_date, from_time, to_time, demand, task_code
FROM register_demand_interval
WHERE to_char(target_date, 'YYYY-MM') = #{targetMonth}

  AND store_code = #{storeCode}

ORDER BY target_date, from_time

mapper.delete:
DELETE FROM register_demand_interval
WHERE store_code = #{storeCode}
  AND target_date = #{targetDate}
