mapper.select:
select 

from public.register
where store_code = #{storeCode,jdbcType=VARCHAR}
  and register_no = #{registerNo,jdbcType=INTEGER}

mapper.delete:
delete from public.register
where store_code = #{storeCode,jdbcType=VARCHAR}
  and register_no = #{registerNo,jdbcType=INTEGER}

mapper.update:
update public.register
set register_name = #{registerName,jdbcType=VARCHAR},
  short_name = #{shortName,jdbcType=VARCHAR},
  open_priority = #{openPriority,jdbcType=INTEGER},
  register_type = #{registerType,jdbcType=VARCHAR},
  is_auto_open_target = #{isAutoOpenTarget,jdbcType=BOOLEAN},
  max_allowance = #{maxAllowance,jdbcType=INTEGER}
where store_code = #{storeCode,jdbcType=VARCHAR}
  and register_no = #{registerNo,jdbcType=INTEGER}
