mapper.insert:
insert into public.task_assignment (
  task_id, employee_code, start_at, end_at, source, status, created_by, created_at
) values (
  #{taskId}, #{employeeCode}, #{startAt}, #{endAt}, #{source}, #{status}, #{createdBy}, #{createdAt}
)

mapper.select:
select 
from public.task_assignment
where employee_code = #{employeeCode}
  and start_at >= #{from}
  and start_at < #{to}
order by start_at

select count(*)
from public.task_assignment a
where a.employee_code = #{employeeCode}
  and a.end_at > #{startAt}
  and a.start_at < #{endAt}
